function degreesToRadians(t){return t*Math.PI/180}function distanceBetweenCoordinates(t,e,o,a){let n=degreesToRadians(o-t),i=degreesToRadians(a-e);t=degreesToRadians(t),o=degreesToRadians(o);let s=Math.sin(n/2)*Math.sin(n/2)+Math.sin(i/2)*Math.sin(i/2)*Math.cos(t)*Math.cos(o);return 6371e3*(2*Math.atan2(Math.sqrt(s),Math.sqrt(1-s)))}class Coordinate{constructor(t,e){this.latitude=t,this.longitude=e}distance(t){return Coordinate.distanceBetween(this,t)}static distanceBetween(t,e){return distanceBetweenCoordinates(t.latitude,t.longitude,e.latitude,e.longitude)}}class Geo{constructor(){this.watchId=null}startGeoLocation(){this.watchId=window.navigator.geolocation.watchPosition(function(t){let e={latitude:t.coords.latitude,longitude:t.coords.longitude};window.document.dispatchEvent(new CustomEvent("geo-coordinate",{detail:e}))},function(t){},{enableHighAccuracy:!0})}stopGeoLocation(){null!==this.watchId&&window.navigator.geolocation.clearWatch(this.watchId)}}export{Coordinate,Geo};